import React from "react";
import "../styles/Login.css";
import { useAuth } from "../components/context/AuthContext.jsx"; // Importa o contexto de autenticação
import { Link } from "react-router-dom"; // Importa o Link para redirecionamento

const Login = () => {
  const { login } = useAuth();

  const handleLogin = (event) => {
    event.preventDefault();
    login(); // Simula o login
    alert("Login successful!");
  };

  return (
    <div className="login-page">
      <h2>Welcome Back!</h2>
      <form className="login-form" onSubmit={handleLogin}>
        <div className="form-group">
          <label>Email</label>
          <input type="email" placeholder="Enter your email id" required />
        </div>
        <div className="form-group">
          <label>Password</label>
          <input type="password" placeholder="Enter your password" required />
        </div>
        <button type="submit" className="login-button">
          Login
        </button>
      </form>
      <p className="register-link">
        Not registered yet? <Link to="/register">Create an account</Link> {/* Atualizado para usar Link */}
      </p>
    </div>
  );
};

export default Login;
