import React from "react";
import Header from "../components/Header";
import Footer from "../components/Footer";
import "../styles/Login.css";
import { useAuth } from "../components/context/AuthContext.jsx"; // Importa o contexto de autenticação

const Login = () => {
  const { login } = useAuth();

  const handleLogin = (event) => {
    event.preventDefault();
    login(); // Simula o login
    alert("Login successful!");
  };

  return (
    <>
      <Header hideLoginButton={true} /> {/* Oculta o botão de Login no Header */}
      <div className="login-page">
        <h2>Welcome Back!</h2>
        <form className="login-form" onSubmit={handleLogin}>
          <div className="form-group">
            <label>Email</label>
            <input type="email" placeholder="Enter your email id" required />
          </div>
          <div className="form-group">
            <label>Password</label>
            <input type="password" placeholder="Enter your password" required />
          </div>
          <button type="submit" className="login-button">
            Login
          </button>
        </form>
        <p className="register-link">
          Not registered yet? <a href="/register">Create an account</a>
        </p>
      </div>
      <Footer />
    </>
  );
};

export default Login;
