import React, { useState } from "react";
import { useAuth } from "../components/context/AuthContext";
import "../styles/PhysioProfile.css"; // Mantém os estilos externos

const PhysioProfile = () => {
  const { user } = useAuth();
  const [activeTab, setActiveTab] = useState("porReceber");
  const [dropdownOpen, setDropdownOpen] = useState(false);

  return (
    <>
      {/* Menu suspenso para perfil */}
      <div 
        className="profile-menu" 
        onMouseEnter={() => setDropdownOpen(true)} 
        onMouseLeave={() => setDropdownOpen(false)}
      >
        <button className="profile-button">Perfil do Utilizador</button>
        {dropdownOpen && (
          <ul className="dropdown-menu">
            <li><a href="#">Editar Perfil</a></li>
            <li><a href="#">Dados para Pagamento de Honorários</a></li>
            <li><a href="#">Inserir Período de Ausência</a></li>
            <li><a href="#">Terminar Sessão</a></li>
          </ul>
        )}
      </div>

      <section className="profile-form-section">
        <h2>Perfil do Fisioterapeuta</h2>
        <p>Aqui estão as informações associadas à sua conta.</p>
        <div className="profile-form">
          <div className="form-row">
            <div className="form-group">
              <label>Nome Completo</label>
              <input type="text" value={user?.name || "Profissional de Saúde"} readOnly />
            </div>
            <div className="form-group">
              <label>Nome de utilizador</label>
              <input type="text" value={user?.username || "N/A"} readOnly />
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default PhysioProfile;
