import React, { useState } from "react";
import { useAuth } from "../components/context/AuthContext";
import "../styles/PhysioProfile.css";

const PhysioProfile = () => {
  const { user } = useAuth();
  const [showDropdown, setShowDropdown] = useState(false);

  return (
    <>
      {/* Barra de navegação */}
      <nav className="navbar">
        <ul className="nav-links">
          <li
            className="nav-item dropdown"
            onMouseEnter={() => setShowDropdown(true)}
            onMouseLeave={() => setShowDropdown(false)}
          >
            <button className="nav-button">Perfil do Utilizador</button>
            {showDropdown && (
              <ul className="dropdown-menu">
                <li><a href="/editar-perfil">Editar Perfil</a></li>
                <li><a href="/pagamento-honorarios">Dados para Pagamento de Honorários</a></li>
                <li><a href="/periodo-ausencia">Inserir Período de Ausência</a></li>
                <li><a href="/logout">Terminar Sessão</a></li>
              </ul>
            )}
          </li>
          <li><a href="/documentacao">Documentação</a></li>
          <li><a href="/tratamentos">Tratamentos</a></li>
          <li><a href="/sessoes">Sessões</a></li>
          <li><a href="/honorarios">Honorários</a></li>
          <li><a href="/manual">Manual de Boas Práticas</a></li>
        </ul>
      </nav>

      <section className="profile-form-section">
        <h2>Perfil do Fisioterapeuta</h2>
        <p>Aqui estão as informações associadas à sua conta.</p>
        <div className="profile-form">
          <div className="form-row">
            <div className="form-group">
              <label>Nome Completo</label>
              <input type="text" value={user?.name || "Profissional de Saúde"} readOnly />
            </div>
            <div className="form-group">
              <label>Nome de utilizador</label>
              <input type="text" value={user?.username || "N/A"} readOnly />
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default PhysioProfile;
